            className="ui button">Toggle Dropdown</button>
        {visible ? 
        <Dropdowm /> : null
        }
        <h2><font color={selected.value}>This is current Color</font></h2>
    </div>);
}
*/
export default ()=>{
    const [selected,setSelected]=useState(options[0]);
    return(<div className="ui container">
        <Header/>
        <Route path='/'><Accordion items={items}/></Route>
        <Route path='/translate'><Translate/></Route>
        <Route path='/dropdown'><Dropdown options={options} selected={selected} onSelectChange={setSelected} Title={"Select a Color"}/></Route>
        <Route path='/search'><Search/></Route>
    </div>);

}
